INSERT INTO `STEP` VALUES (10,'XmlTransformationStep','Missing non-protocol filtering ',1,'OAIPMH-DC to PZ','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\r\n<!--\r\n\r\n    This stylesheet expects oai/dc records\r\n-->\r\n<xsl:stylesheet\r\n    version=\"1.0\"\r\n    xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\"\r\n    xmlns:pz=\"http://www.indexdata.com/pazpar2/1.0\"\r\n    xmlns:oai=\"http://www.openarchives.org/OAI/2.0/\"\r\n    xmlns:dc=\"http://purl.org/dc/elements/1.1/\"\r\n    xmlns:dcterms=\"http://purl.org/dc/terms/\">\r\n\r\n  <xsl:output indent=\"yes\"\r\n              method=\"xml\"\r\n              version=\"1.0\"\r\n              encoding=\"UTF-8\"/>\r\n\r\n  <xsl:template match=\"/oai:OAI-PMH\">\r\n    <xsl:apply-templates/>\r\n  </xsl:template>\r\n\r\n  <xsl:template match=\"oai:ListRecords\">\r\n    <pz:collection>\r\n      <xsl:apply-templates/>\r\n    </pz:collection>\r\n  </xsl:template>\r\n\r\n  <xsl:template match=\"oai:record\">\r\n    <pz:record>\r\n      <pz:metadata type=\"id\">\r\n        <xsl:value-of select=\"oai:header/oai:identifier\"/>\r\n      </pz:metadata>\r\n      <xsl:if test=\"oai:header[@status=\'deleted\']\">\r\n	<pz:metadata type=\"record_status\">deleted</pz:metadata>\r\n      </xsl:if>\r\n      <xsl:apply-templates/>\r\n    </pz:record>\r\n  </xsl:template>\r\n    \r\n  <xsl:template match=\"oai:metadata/*\">\r\n    <xsl:for-each select=\"dc:title\">\r\n      <pz:metadata type=\"title\">\r\n        <xsl:value-of select=\".\"/>\r\n      </pz:metadata>\r\n    </xsl:for-each>\r\n\r\n    <xsl:for-each select=\"dc:date\">\r\n      <pz:metadata type=\"date\">\r\n	<xsl:value-of select=\".\"/>\r\n      </pz:metadata>\r\n    </xsl:for-each>\r\n\r\n    <xsl:for-each select=\"dc:subject\">\r\n      <pz:metadata type=\"subject\">\r\n	<xsl:value-of select=\".\"/>\r\n      </pz:metadata>\r\n    </xsl:for-each>\r\n\r\n    <xsl:for-each select=\"dc:creator\">\r\n      <pz:metadata type=\"author\">\r\n        <xsl:value-of select=\".\"/>\r\n      </pz:metadata>\r\n    </xsl:for-each>\r\n\r\n    <xsl:for-each select=\"dc:description\">\r\n      <pz:metadata type=\"description\">\r\n	<xsl:value-of select=\".\"/>\r\n      </pz:metadata>\r\n    </xsl:for-each>\r\n\r\n    <xsl:for-each select=\"dc:identifier\">\r\n      <pz:metadata type=\"electronic-url\">\r\n	<xsl:value-of select=\".\"/>\r\n      </pz:metadata>\r\n    </xsl:for-each>\r\n\r\n    <xsl:for-each select=\"dc:type\">\r\n      <pz:metadata type=\"medium\">\r\n	<xsl:value-of select=\".\"/>\r\n      </pz:metadata>\r\n    </xsl:for-each>\r\n      \r\n    <xsl:for-each select=\"dcterms:bibliographicCitation\">\r\n      <pz:metadata type=\"citation\">\r\n        <xsl:value-of select=\".\"/>\r\n      </pz:metadata>\r\n    </xsl:for-each>\r\n\r\n  </xsl:template>\r\n\r\n  <xsl:template match=\"text()\"/>\r\n\r\n</xsl:stylesheet>','','','','PZ','OAI-PMH(DC)',''),(20,'XmlTransformationStep','',1,'OAIPMH-MARC to MARC21','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\r\n<!--\r\n\r\n    This stylesheet pulls out marc records from the oai-pmh response\r\n    and overwrites the controlfield 001 with oai-identifier\r\n-->\r\n<xsl:stylesheet\r\n    version=\"1.0\"\r\n    xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\"\r\n    xmlns:oai=\"http://www.openarchives.org/OAI/2.0/\"\r\n    xmlns:marc=\"http://www.loc.gov/MARC21/slim\"\r\n    xmlns:pz=\"http://www.indexdata.com/pazpar2/1.0\"\r\n    exclude-result-prefixes=\"oai\">\r\n\r\n\r\n <xsl:output indent=\"yes\"\r\n        method=\"xml\"\r\n        version=\"1.0\"\r\n        encoding=\"UTF-8\"/>\r\n\r\n\r\n<xsl:template match=\"/\">\r\n<collection>\r\n  <xsl:apply-templates/>\r\n</collection>\r\n\r\n</xsl:template>\r\n\r\n  <xsl:template match=\"//oai:record\">\r\n    <xsl:if test=\"oai:header[@status=\'deleted\']\">\r\n      <delete><xsl:attribute name=\"id\"><xsl:value-of select=\"oai:header/oai:identifier\"/></xsl:attribute></delete>\r\n    </xsl:if>\r\n    <xsl:if test=\"not(oai:header[@status=\'deleted\'